import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#8B4513', // Saddle Brown - evokes warmth, like natural scents
    },
    secondary: {
      main: '#FFC107', // Amber - bright, attention-catching, complements brown
    },
    background: {
      default: '#FFF5E1', // Light Beige - gives a sense of lightness and elegance
    },
    text: {
      primary: '#333', // Dark Grey - neutral color for better readability
      secondary: '#555', // Medium Grey - slightly softer for supporting text
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 500,
    },
    body1: {
      lineHeight: 1.6,
    },
  },
});

export default theme;
