import React, { useState } from 'react';
import { Box, Typography, Button } from '@mui/material';
import AuthSidebar from './components/AuthSidebar';

function HomePage() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const toggleDrawer = (open) => () => {
    setIsDrawerOpen(open);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'flex-start',
        height: '100vh',
        padding: 4,
        backgroundColor: (theme) => theme.palette.background.default,
      }}
    >
      <Typography variant="h1" color="primary" gutterBottom sx={{ marginTop: '5vh' }}>
        Fragrance Planet
      </Typography>
      <Typography variant="h2" color="primary" gutterBottom>
        Find Your Perfect Fragrance
      </Typography>
      <Typography variant="body1" color="text.secondary" align="center" sx={{ marginTop: 2 }}>
        Discover scents that suit your personality. Let us recommend something special for you.
      </Typography>
      <Button variant="contained" color="secondary" sx={{ marginTop: 4 }}>
        Browse Now
      </Button>

      {/* Button to toggle Sidebar */}
      <Button variant="text" color="primary" onClick={toggleDrawer(true)} sx={{ position: 'fixed', top: 16, right: 16 }}>
        Login
      </Button>

      {/* Auth Sidebar Component */}
      <AuthSidebar isDrawerOpen={isDrawerOpen} toggleDrawer={toggleDrawer} />
    </Box>
  );
}

export default HomePage;
