import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#4A148C', // Deep Purple
    },
    secondary: {
      main: '#1976D2', // Blue
    },
    background: {
      default: '#EDE7F6', // Light Purple background
    },
    text: {
      primary: '#1A237E', // Dark Blue for strong contrast
      secondary: '#311B92', // Purple for subtler text
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 600,
      fontSize: '2.5rem', // Larger font size for main headline
    },
    body1: {
      fontSize: '1.2rem', // Increase body text size slightly
      lineHeight: 1.8,
    },
    button: {
      fontSize: '1.1rem',
    },
  },
});

export default theme;
