import React, { useState } from 'react';
import { Box, Typography, Button, Drawer, List, ListItem, ListItemText, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

function HomePage() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const toggleDrawer = (open) => () => {
    setIsDrawerOpen(open);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'flex-start',
        height: '100vh',
        padding: 4,
        backgroundColor: (theme) => theme.palette.background.default,
      }}
    >
      {/* Header with Login and Sign Up buttons */}
      <Box sx={{ display: 'flex', width: '100%', justifyContent: 'space-between', padding: '16px 32px', position: 'fixed', top: 0, backgroundColor: '#4a148c', zIndex: 10 }}>
        <Typography variant="h6" color="inherit" sx={{ color: 'white' }}>
          Fragrance Planet
        </Typography>
        <Box>
          <Button color="inherit" sx={{ color: 'white' }} onClick={() => window.location.href = '/login'}>Login</Button>
          <Button color="inherit" sx={{ color: 'white' }} onClick={() => window.location.href = '/signup'}>Sign Up</Button>
        </Box>
      </Box>

      <Box sx={{ marginTop: '20vh', textAlign: 'center' }}>
        <Typography variant="h1" color="primary" gutterBottom>
          Fragrance Planet
        </Typography>
        <Typography variant="h2" color="primary" gutterBottom>
          Find Your Perfect Fragrance
        </Typography>
        <Typography variant="body1" color="text.secondary" align="center" sx={{ marginTop: 2 }}>
          Discover scents that suit your personality. Let us recommend something special for you.
        </Typography>
        <Button variant="contained" color="secondary" sx={{ marginTop: 4 }}>
          Browse Now
        </Button>
      </Box>

      {/* Icon Button to toggle Sidebar */}
      <IconButton color="primary" onClick={toggleDrawer(true)} sx={{ position: 'fixed', top: 16, right: 16 }}>
        <MenuIcon />
      </IconButton>

      {/* Auth Sidebar Component */}
      <Drawer anchor="right" open={isDrawerOpen} onClose={toggleDrawer(false)}>
        <Box
          sx={{ width: 250 }}
          role="presentation"
          onClick={toggleDrawer(false)}
          onKeyDown={toggleDrawer(false)}
        >
          <List>
            <ListItem button onClick={() => window.location.href = '/login'}>
              <ListItemText primary="Login" />
            </ListItem>
            <ListItem button onClick={() => window.location.href = '/signup'}>
              <ListItemText primary="Sign Up" />
            </ListItem>
          </List>
        </Box>
      </Drawer>
    </Box>
  );
}

export default HomePage;
